// FlightSearchForm.js
import React, { useState } from 'react';

const FlightSearchForm = ({ onSearchResult }) => {
  const [sourceCity, setSourceCity] = useState('');
  const [destinationCity, setDestinationCity] = useState('');
  const [travelDate, setTravelDate] = useState('');
  const [returnDate, setReturnDate] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform validation if needed
    // Make API call to fetch search results
    const searchParams = {
      sourceCity,
      destinationCity,
      travelDate,
      returnDate,
    };
    // Assuming fetchSearchResults is a function that fetches search results from backend
    fetchSearchResults(searchParams);
  };

  const fetchSearchResults = (params) => {
    // Make API call to backend with search parameters
    // This is just a placeholder
    const mockSearchResult = [
      {
        flightNumber: 'ABC123',
        airlineName: 'Sample Airlines',
        departureTime: '10:00 AM',
        arrivalTime: '12:00 PM',
        duration: '2 hours',
        numberOfStops: 1,
        price: '$200',
      },
      // Add more search results here
    ];
    onSearchResult(mockSearchResult);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Source City"
          value={sourceCity}
          onChange={(e) => setSourceCity(e.target.value)}
          required
        />
        <input
          type="text"
          placeholder="Destination City"
          value={destinationCity}
          onChange={(e) => setDestinationCity(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Travel Date"
          value={travelDate}
          onChange={(e) => setTravelDate(e.target.value)}
          required
        />
        <input
          type="date"
          placeholder="Return Date"
          value={returnDate}
          onChange={(e) => setReturnDate(e.target.value)}
        />
        <button type="submit">Search</button>
      </form>
    </div>
  );
};

export default FlightSearchForm;
